function createVariables () {
    LookingDirection = "Middle"
    MiddleDoorOpen = false
    Can_Enable_Cams = true
    Camera_activated = false
    Hours = 12
    Minutes = -1
}
function OfficeDirection (direction: string, CurrentDirection: string) {
    if (CurrentDirection == "Middle") {
        if (direction == "Left") {
            LookingDirection = "Left"
        } else if (direction == "Right") {
            LookingDirection = "Right"
        }
    } else if (CurrentDirection == "Left") {
        if (direction == "Left") {
            LookingDirection = "Left"
        } else if (direction == "Right") {
            LookingDirection = "Middle"
        }
    } else if (CurrentDirection == "Right") {
        if (direction == "Left") {
            LookingDirection = "Middle"
        } else if (direction == "Right") {
            LookingDirection = "Right"
        }
    }
}
