controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    if (Camera_activated) {
        change_Cams("Up")
    }
})
sprites.onOverlap(SpriteKind.Mouse, SpriteKind.Lever, function (sprite, otherSprite) {
    if (controller.A.isPressed()) {
        timer.throttle("using door", 50, function () {
            if (!(MiddleDoorClosed)) {
                if (controller.down.isPressed()) {
                    MiddleDoorClosed = true
                    Lever.setImage(assets.image`Enabled_Lever`)
                    Middle_Door.setImage(assets.image`ClosedMiddleDoor`)
                }
            } else {
                if (controller.up.isPressed()) {
                    MiddleDoorClosed = false
                    Lever.setImage(assets.image`Disabled_Lever`)
                    Middle_Door.setImage(assets.image`OpenMiddleDoor`)
                }
            }
        })
    }
})
controller.B.onEvent(ControllerButtonEvent.Pressed, function () {
    if (!(Flashlight_Enabled)) {
        Flashlight_Enabled = true
        Flashlight_Battery.setFlag(SpriteFlag.Invisible, false)
    } else {
        Flashlight_Enabled = false
        Flashlight_Battery.setFlag(SpriteFlag.Invisible, true)
    }
})
