function change_Cams (Direction_pressed: string) {
    if (Direction_pressed == "Right") {
        if (Current_Cam == 1) {
            Current_Cam = 4
        } else {
            Current_Cam += 1
        }
    } else if (Direction_pressed == "Left") {
        if (Current_Cam == 1) {
            Current_Cam = 2
        } else {
            Current_Cam += -1
        }
    } else if (Direction_pressed == "Up") {
        if (Current_Cam == 3) {
            Current_Cam = 1
        } else if (Current_Cam == 4) {
            Current_Cam = 1
        } else {
            Current_Cam += -2
        }
    } else if (Direction_pressed == "Down") {
        if (Current_Cam == 1) {
            Current_Cam = 3
        } else if (Current_Cam == 2) {
            Current_Cam = 3
        } else {
            Current_Cam += 2
        }
    }
}
function createVariables () {
    LookingDirection = "Middle"
    MiddleDoorClosed = false
    Can_Enable_Cams = true
    Camera_activated = false
    Hours = 12
    Minutes = -1
    Flashlight_Enabled = false
    Current_Cam = 1
}
function OfficeDirection (direction: string, CurrentDirection: string) {
    if (CurrentDirection == "Middle") {
        if (direction == "Left") {
            LookingDirection = "Left"
        } else if (direction == "Right") {
            LookingDirection = "Right"
        }
    } else if (CurrentDirection == "Left") {
        if (direction == "Left") {
            LookingDirection = "Left"
        } else if (direction == "Right") {
            LookingDirection = "Middle"
        }
    } else if (CurrentDirection == "Right") {
        if (direction == "Left") {
            LookingDirection = "Middle"
        } else if (direction == "Right") {
            LookingDirection = "Right"
        }
    }
}
