game.onUpdate(function () {
    timer.throttle("counting timer", 1000, function () {
        Minutes += 1
    })
    if (Hours == 6) {
        game.gameOver(true)
    }
    if (Minutes == 60) {
        Minutes = 0
        if (Hours != 12) {
            Hours += 1
        } else {
            Hours = 1
        }
    }
    Clock.setText("AM " + Hours + ":" + Minutes)
    if (Camera_activated) {
        controller.moveSprite(Mouse_Spr, 0, 0)
    } else {
        controller.moveSprite(Mouse_Spr, 150, 150)
    }
})
game.onUpdate(function () {
    if (LookingDirection == "Left") {
        Can_Enable_Cams = false
        Office.setImage(assets.image`Left_Room`)
        Left_arrow.setFlag(SpriteFlag.Invisible, true)
        Lever.setFlag(SpriteFlag.Invisible, true)
        Middle_Door.setFlag(SpriteFlag.Invisible, true)
        Camera_Side.setFlag(SpriteFlag.Invisible, true)
    } else if (LookingDirection == "Middle") {
        Can_Enable_Cams = true
        Office.setImage(assets.image`Middle_Office`)
        Left_arrow.setFlag(SpriteFlag.Invisible, false)
        Right_arrow.setFlag(SpriteFlag.Invisible, false)
        Lever.setFlag(SpriteFlag.Invisible, false)
        Middle_Door.setFlag(SpriteFlag.Invisible, false)
        Camera_Side.setFlag(SpriteFlag.Invisible, false)
    } else if (LookingDirection == "Right") {
        Can_Enable_Cams = false
        if (Flashlight_Enabled) {
            Office.setImage(assets.image`LightedRightRoom`)
        } else {
            Office.setImage(assets.image`Right_Room`)
        }
        Right_arrow.setFlag(SpriteFlag.Invisible, true)
        Lever.setFlag(SpriteFlag.Invisible, true)
        Middle_Door.setFlag(SpriteFlag.Invisible, true)
        Camera_Side.setFlag(SpriteFlag.Invisible, true)
    }
})
game.onUpdate(function () {
    if (Flashlight_Enabled) {
        timer.throttle("action", 1000, function () {
            Flashlight_Battery.value += -1
        })
    }
})
